//VERSION=3
let minVal = 0.0;
let maxVal = 1.0;

let viz = new DefaultVisualizer(minVal, maxVal);

function evaluatePixel(samples) {
    let val = samples.GIFAPAR;
    val = viz.process(val);
    return [val, samples.dataMask];
}

function setup() {
  return {
    input: [{
      bands: [
        "GIFAPAR",
        "dataMask"
      ]
